namespace rvinowise.twitter.test

open System
open Xunit
open FsUnit
open rvinowise.html_parsing
open rvinowise.twitter



module quotation_test =
    
    
    let ``parse image-post with a quotation of a 4-video-post`` post =
        post.id
        |>should equal (Post_id 1704659922464444801L)
        
        post
        |>Main_post.message
        |>should equal (Post_message.Full "image-post with a quotation of a 4-video-post")
        
        post
        |>Main_post.header
        |>Post_header.author
        |>should equal {
            name="tehprom"
            handle = User_handle "tehprom269887"
        }
        
        post
        |>Main_post.header
        |>Post_header.created_at
        |>should equal (DateTime(2023,9,21,0,52,51))
        
        post
        |>Main_post.media_load
        |>should equal [
            Media_item.Image{
                Posted_image.url="https://pbs.twimg.com/media/F6grJf8XYAAvmS-"
                description = "Image"
            }
        ]
        
        
        let check_quoted_post (quoted_post:Quotable_message) =
            
            let expected_message =
                Post_message.Full """Ex-Muslim women and former hijabis are opening up about their religious trauma in this recent TikTok trend.

It’s so empowering to see more and more courageous women finding the freedom to be their unapologetic selves ❤️

#FreeFromHijab"""
            
            quoted_post.header.author
            |>should equal {
                name="Infidel Noodle"
                handle = User_handle "InfidelNoodle"
            }
            quoted_post.message
            |>should equal expected_message

            quoted_post.media_load
            |>should haveLength 4
            
            quoted_post.media_load
            |>should equal [
                Media_item.Video_poster "https://pbs.twimg.com/ext_tw_video_thumb/1637403599469948929/pu/img/oRus-51SqWsSwSNH"
                Media_item.Video_poster "https://pbs.twimg.com/ext_tw_video_thumb/1637403599675465729/pu/img/_AlaRyOnmno3YwzU"
                Media_item.Video_poster "https://pbs.twimg.com/ext_tw_video_thumb/1637403599478325248/pu/img/B39MLxbER9pQyx9z"
                Media_item.Video_poster "https://pbs.twimg.com/ext_tw_video_thumb/1637403599469948928/pu/img/yT-saemhQM1qb6iP"
            ]
        
        post
        |>Main_post.external_source
        |>function
        |Some (Quoted_message quoted_post) ->
            check_quoted_post quoted_post
        |_ -> raise <| Bad_post_exception()
        
        
        post.stats.likes
        |>should equal 1
        post.stats.reposts
        |>should equal 0
        post.stats.replies
        |>should equal 1
        
    
