                description="Image"
            }
            Media_item.Image {
                url="https://pbs.twimg.com/media/F62VrsAWcAAxUor"
                description="Image"
            }
        ]
        
        let check_quoted_post (quoted_post:Quotable_message) =
            
            quoted_post.header.author
            |>should equal {
                name="tehprom"
                handle = User_handle "tehprom269887"
            }
            quoted_post.message
            |>should equal (Post_message.Full "an image-reply to my image-post")
            quoted_post.media_load
            |>should equal [
                Media_item.Image {
                    url="https://pbs.twimg.com/media/F61myJuWkAArDgv"
                    description="Image"
                }
                Media_item.Image {
                    url="https://pbs.twimg.com/media/F61mz2MWsAAN5CY"
                    description="Image"
                }
            ]
            if
                {
                    Reply.to_user=User_handle "tehprom269887"
                    to_post=None
                    is_direct=true
                }
                |>Some
                |>(<>) quoted_post.header.reply
            then
                raise <| Bad_post_exception()
        
        post
        |>Main_post.external_source
        |>function
        |Some (Quoted_message quoted_post) ->
            check_quoted_post quoted_post
        | _ -> raise <| Bad_post_exception()
    
